import React, { useEffect, useState } from 'react';
import {Link} from 'react-router-dom';
import './ProductPage.css';

const ProductPage = () => {
    const[products, setProducts] = useState([]);

    useEffect(() => {
        getProducts();
    }, [])
    
    const getProducts = async () => {
        let result = await fetch('http://localhost:5000/');
        result = await result.json();
        setProducts(result);
    }

    const deleteProduct = async (id) => {
      let deleteItem = await fetch(`http://localhost:5000/product/${id}`, {
        method: 'delete',
      });    
      deleteItem = await deleteItem.json();  
      if(deleteItem){
        alert('product deleted');
        getProducts();
      }
    }

    const updateProduct =  () => {
      alert('update Product')
    }

    return (
    <div className="table-container">
        <h1><center>Product Information</center></h1>
      <table className="responsive-table">
        <thead>
          <tr>
            <th>Product No</th>
            <th>Product Name</th>
            <th>Product Price</th>
            <th>Product Category</th>
            <th>Product Company</th>
            <th>Delete Record</th>
            <th>Update Record</th>
          </tr>
        </thead>
        <tbody>
            {
            products.map((item, index) => 
                <tr key={item}>
                    <td>{index+1}</td>
                    <td>{item.name}</td>
                    <td>{item.price}</td>
                    <td>{item.category}</td>
                    <td>{item.company}</td>
                    <td><button onClick={() => deleteProduct(item._id)} style={{backgroundColor: 'red'}}>Delete</button></td>
                    <td><button onClick={() => updateProduct(item._id)} style={{backgroundColor: '#0056b3'}}><Link to="/update" className="nav-link" style={{color: 'white'}}>Update</Link></button></td>
                </tr> 
            )
            }
        </tbody>
      </table>
    </div>
  );
};

export default ProductPage;
